import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './HistoryPage.css';

function HistoryPage() {
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Fetch history on mount
  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = () => {
    axios.get('http://localhost:5000/api/history')
      .then((res) => {
        setHistory(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('History fetch error:', err);
        setLoading(false);
      });
  };

  // Delete an individual search item
  const deleteHistoryItem = (id) => {
    axios.delete(`http://localhost:5000/api/history/${id}`)
      .then(() => {
        setHistory(prev => prev.filter(item => item.id !== id));
      })
      .catch((err) => {
        console.error('Error deleting item:', err);
      });
  };

  if (loading) {
    return <div className="loading">Loading history...</div>;
  }

  return (
    <div className="history-page">
      <div className="header">
        <button onClick={() => navigate('/')} className="back-btn">Back to Main Page</button>
      </div>

      <h2>Search History</h2>

      {history.length === 0 ? (
        <div className="no-history">No search history found.</div>
      ) : (
        <ul className="history-list">
          {history.map((item) => (
            <li key={item.id} className="history-item">
              <div className="history-details">
                <div><strong>City:</strong> {item.city}</div>
                <div><strong>Search Date:</strong> {new Date(item.searched_at).toLocaleString()}</div>
              </div>
              <button
                className="delete-btn"
                onClick={() => deleteHistoryItem(item.id)}
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default HistoryPage;
