import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './HistoryPage.css';

function HistoryPage() {
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Fetch history data from the backend
  useEffect(() => {
    axios.get('http://localhost:5000/api/history')
      .then((res) => {
        setHistory(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('History fetch error:', err);
        setLoading(false);
      });
  }, []);

  // Clear search history
  const clearHistory = () => {
    axios.delete('http://localhost:5000/api/history') // Endpoint to clear history
      .then(() => {
        setHistory([]); // Clear the history state
      })
      .catch((err) => {
        console.error('Error clearing history:', err);
      });
  };

  // Loading state
  if (loading) {
    return <div className="loading">Loading history...</div>;
  }

  return (
    <div className="history-page">
      <div className="header">
        <button onClick={() => navigate('/')} className="back-btn">Back to Main Page</button>
        <button onClick={clearHistory} className="clear-btn">Clear History</button>
      </div>

      <h2>Search History</h2>

      {/* No history available */}
      {history.length === 0 && <div className="no-history">No search history found.</div>}

      <ul className="history-list">
        {history.map((item, index) => (
          <li key={index} className="history-item">
            <div className="history-details">
              <div><strong>City:</strong> {item.city}</div>
              <div><strong>Search Date:</strong> {new Date(item.searched_at).toLocaleString()}</div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default HistoryPage;
