import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function HistoryPage() {
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true); // Loading state

  useEffect(() => {
    axios.get('http://localhost:5000/api/history')
      .then((res) => {
        setHistory(res.data);
        setLoading(false); // Set loading to false once data is fetched
      })
      .catch((err) => {
        console.error('History fetch error:', err);
        setLoading(false); // Handle error and set loading to false
      });
  }, []);

  if (loading) {
    return <div className="loading">Loading history...</div>;
  }

  return (
    <div className="App">
      <h2>Search History</h2>

      {/* No history available */}
      {history.length === 0 && <div>No search history found.</div>}

      <ul className="history-list">
        {history.map((item, index) => (
          <li key={index} className="history-item">
            <div><strong>City:</strong> {item.city}</div>
            <div><strong>Temperature:</strong> {item.temperature}Â°C</div>
            <div><strong>Weather:</strong> {item.weather}</div>
            <div><strong>Humidity:</strong> {item.humidity}%</div>
            <div><strong>Search Date:</strong> {new Date(item.searched_at).toLocaleString()}</div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default HistoryPage;
